mapper.select:
		 select *
      from
      (
	      select rownum as RNUM, A.* from
	      (		
			select * from signListView
			
			where m_board_no = #{MBoardNo}
			order by doc_no desc
	      )A
      )
      
      where RNUM> #{firstRecordIndex} 
      and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update sign
set status ='처리중'
where doc_no = #{docNo}

update sign
set status ='완료'
where doc_no = #{docNo}

mapper.select:
