sql.where:
status = '처리중'

	and doc_title like '%' || #{searchTitle} || '%'


	and dept_no = #{searchDeptNo}


	and mem_name = #{searchName}

status = '완료'

	and doc_title like '%' || #{searchTitle} || '%'


	and dept_no = #{searchDeptNo}


	and mem_name = #{searchName}

mapper.update:
update sign
set doc_title= #{docTitle}, doc_content= #{docContent}
where doc_no = #{docNo}
