mapper.update:
update attendance 
set out_time = sysdate,
status = #{status}
where attendance_no = #{attendanceNo}

mapper.select:
SELECT *
FROM attendance
WHERE TO_CHAR(in_time, 'YYYY-MM') = #{date}
AND mem_no = #{memNo}
ORDER BY attendance_no DESC

SELECT count(*)
FROM attendance
WHERE TO_CHAR(in_time, 'YYYY-MM') = #{date}
AND mem_no = #{memNo}
and status like '%지각%'

SELECT count(*)
FROM attendance
WHERE TO_CHAR(in_time, 'YYYY-MM') = #{date}
AND mem_no = #{memNo}
and status like '%출근%'

SELECT count(*)
FROM attendance
WHERE TO_CHAR(in_time, 'YYYY-MM') = #{date}
AND mem_no = #{memNo}
and status like '%조퇴%'
