mapper.select:
    	SELECT * FROM 
    	(
    		SELECT ROWNUM AS RNUM, A.* FROM 
    		(
        		SELECT * FROM MBOARDLISTINFOVIEW 

        		ORDER BY M_BOARD_NAME
    		)A
		)


		WHERE RNUM > #{firstRecordIndex} 
		AND RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update M_BOARD
SET MEM_NO = #{memNo}, 
M_BOARD_NAME = #{MBoardName}
where M_BOARD_NO=#{MBoardNo}
