mapper.select:
select *
from
(
	select rownum as RNUM, A.* from
	(
		select * from staff


		order by staff_no desc
	)A
)

where RNUM> #{firstRecordIndex} 
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}
and staff_status='Y'

mapper.update:
update staff
set staff_status='N'
where staff_no=#{staffNo}
