mapper.select:
      select *
      from
      (
	      select rownum as RNUM, A.* from
	      (		
			select * from teamNoticeListView

			order by regdate desc
	      )A
      )

      where RNUM> #{firstRecordIndex} 
      and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select * from teamNoticeListView
where team_notice_no=#{teamNoticeNo}
and sortNo is null

mapper.update:
		update team_notice
        set views = views+1
        where team_Notice_no=#{teamNoticeNo}
