mapper.select:
      select *
      from
      (
	      select rownum as RNUM, A.* from
	      (		
			select * from teamNoticeListView

			order by regdate desc
	      )A
      )

      where RNUM> #{firstRecordIndex} 
      and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
		update team_notice
        set views = views+1
        where team_Notice_no=#{teamNoticeNo}

update team_notice
set team_notice_title = #{teamNoticeTitle},
team_notice_content=#{teamNoticeContent}

	,fsize=#{fsize},fileName=#{fileName},
	originname=#{originname}

where team_Notice_No =#{teamNoticeNo}

update team_Notice
set sortNo = sortNo+1
where CONTENTNO=#{contentno} and sortNo>#{sortno}

update team_Notice
set COMMENTS = #{comments}
where team_notice_no=#{teamNoticeNo}

update team_notice
set fsize=0, fileName=null, originname=null
where team_notice_no=#{teamNoticeNo}

mapper.select:
select * from ( select rownum as RNUM, (sysdate-regdate)*24 as dateGap, A.* from ( select * from teamNoticeListView where m_board_no=#{mBoardNo} and status='Y' and step=0 order by regdate desc
)A )
where RNUM<#{lastRecordIndex}
