mapper.select:
SELECT * FROM todoListDetailMemberView
WHERE TODOLIST_NO = #{todolistNo}
AND STATUS ='Y';

mapper.update:
update TODOLIST_DETAIL
set status=#{status}
where TODO_DETAIL_NO=#{todoDetailNo}

update TODOLIST_DETAIL
set mem_no = #{memNo}
where TODO_DETAIL_NO=#{todoDetailNo}

update TODOLIST_DETAIL
set mem_no = null
where TODO_DETAIL_NO=#{todoDetailNo}

update TODOLIST_DETAIL
set TODO_CONTENT= #{todoContent}
where TODO_DETAIL_NO=#{todoDetailNo}
